import{_ as a,c as t,o as s,ag as i}from"./chunks/framework.CQuhCYrb.js";const n="/images/cookbook/chat-ticket-linking.svg",u=JSON.parse('{"title":"Linking Chats to Tickets","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/chat-ticket-linking.md","filePath":"cookbook/chat-ticket-linking.md"}'),l={name:"cookbook/chat-ticket-linking.md"};function o(r,e,c,p,h,d){return s(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="linking-chats-to-tickets" tabindex="-1">Linking Chats to Tickets <a class="header-anchor" href="#linking-chats-to-tickets" aria-label="Permalink to &quot;Linking Chats to Tickets&quot;">​</a></h1><p>Connect conversations to tickets for complete work history and context.</p><p><strong>Difficulty</strong>: Beginner <strong>Time</strong>: 5 minutes</p><h2 id="what-you-ll-learn" tabindex="-1">What You&#39;ll Learn <a class="header-anchor" href="#what-you-ll-learn" aria-label="Permalink to &quot;What You&#39;ll Learn&quot;">​</a></h2><p>How to:</p><ul><li>Link chat sessions to tickets</li><li>Set ticket context before chatting</li><li>Archive sessions to tickets</li><li>View linked conversations</li></ul><p><img src="'+n+`" alt="Chat-Ticket Linking"><em>TBD: Replace with screenshot of linked chat and ticket</em></p><h2 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h2><ul><li>Sciorex installed</li><li>At least one ticket created</li></ul><h2 id="why-link-chats-to-tickets" tabindex="-1">Why Link Chats to Tickets? <a class="header-anchor" href="#why-link-chats-to-tickets" aria-label="Permalink to &quot;Why Link Chats to Tickets?&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Benefit</th><th>Description</th></tr></thead><tbody><tr><td><strong>Context</strong></td><td>Agents understand what they&#39;re working on</td></tr><tr><td><strong>History</strong></td><td>See all conversations about a ticket</td></tr><tr><td><strong>Handoff</strong></td><td>Others can review AI discussions</td></tr><tr><td><strong>Audit</strong></td><td>Track how decisions were made</td></tr></tbody></table><h2 id="method-1-set-context-before-chatting" tabindex="-1">Method 1: Set Context Before Chatting <a class="header-anchor" href="#method-1-set-context-before-chatting" aria-label="Permalink to &quot;Method 1: Set Context Before Chatting&quot;">​</a></h2><h3 id="step-1-open-or-create-a-ticket" tabindex="-1">Step 1: Open or Create a Ticket <a class="header-anchor" href="#step-1-open-or-create-a-ticket" aria-label="Permalink to &quot;Step 1: Open or Create a Ticket&quot;">​</a></h3><ol><li>Go to <strong>Tickets</strong> in the sidebar</li><li>Open an existing ticket or create a new one</li><li>Note the ticket ID (e.g., T-042)</li></ol><h3 id="step-2-start-a-linked-chat" tabindex="-1">Step 2: Start a Linked Chat <a class="header-anchor" href="#step-2-start-a-linked-chat" aria-label="Permalink to &quot;Step 2: Start a Linked Chat&quot;">​</a></h3><ol><li><p>Click <strong>Start Chat</strong> from the ticket view</p><p>Or:</p></li><li><p>Go to <strong>Chat</strong></p></li><li><p>Click <strong>New Chat</strong></p></li><li><p>Click the <strong>Link to Ticket</strong> button</p></li><li><p>Select your ticket</p></li></ol><h3 id="step-3-chat-with-context" tabindex="-1">Step 3: Chat with Context <a class="header-anchor" href="#step-3-chat-with-context" aria-label="Permalink to &quot;Step 3: Chat with Context&quot;">​</a></h3><p>Your agent now has ticket context:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>You: Help me implement this feature</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Agent: I see this is for ticket T-042: &quot;Add user profile avatar upload&quot;</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Based on the requirements:</span></span>
<span class="line"><span>- Accept JPEG, PNG, GIF (max 5MB)</span></span>
<span class="line"><span>- Crop to square aspect ratio</span></span>
<span class="line"><span>...</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Let me start by examining the existing file upload component...</span></span></code></pre></div><p>The agent automatically:</p><ul><li>Reads ticket details</li><li>References requirements</li><li>Understands the scope</li></ul><h2 id="method-2-archive-session-to-ticket" tabindex="-1">Method 2: Archive Session to Ticket <a class="header-anchor" href="#method-2-archive-session-to-ticket" aria-label="Permalink to &quot;Method 2: Archive Session to Ticket&quot;">​</a></h2><p>Link an existing conversation after the fact.</p><h3 id="step-1-complete-your-chat" tabindex="-1">Step 1: Complete Your Chat <a class="header-anchor" href="#step-1-complete-your-chat" aria-label="Permalink to &quot;Step 1: Complete Your Chat&quot;">​</a></h3><p>Have a productive conversation without linking first.</p><h3 id="step-2-archive-the-session" tabindex="-1">Step 2: Archive the Session <a class="header-anchor" href="#step-2-archive-the-session" aria-label="Permalink to &quot;Step 2: Archive the Session&quot;">​</a></h3><ol><li>Click the <strong>Archive</strong> button in the chat header</li><li>In the dialog: <ul><li>Select <strong>Link to Ticket</strong></li><li>Choose the relevant ticket</li><li>Add optional notes</li></ul></li><li>Click <strong>Archive</strong></li></ol><h3 id="step-3-view-in-ticket" tabindex="-1">Step 3: View in Ticket <a class="header-anchor" href="#step-3-view-in-ticket" aria-label="Permalink to &quot;Step 3: View in Ticket&quot;">​</a></h3><p>The session now appears in the ticket&#39;s <strong>Sessions</strong> tab.</p><h2 id="viewing-linked-sessions" tabindex="-1">Viewing Linked Sessions <a class="header-anchor" href="#viewing-linked-sessions" aria-label="Permalink to &quot;Viewing Linked Sessions&quot;">​</a></h2><h3 id="from-ticket-view" tabindex="-1">From Ticket View <a class="header-anchor" href="#from-ticket-view" aria-label="Permalink to &quot;From Ticket View&quot;">​</a></h3><ol><li>Open a ticket</li><li>Click the <strong>Sessions</strong> tab</li><li>See all linked conversations</li><li>Click any session to view/resume</li></ol><h3 id="from-chat-view" tabindex="-1">From Chat View <a class="header-anchor" href="#from-chat-view" aria-label="Permalink to &quot;From Chat View&quot;">​</a></h3><ol><li>Linked tickets show in the chat header</li><li>Click the ticket ID to jump to ticket view</li></ol><h2 id="agent-access-to-tickets" tabindex="-1">Agent Access to Tickets <a class="header-anchor" href="#agent-access-to-tickets" aria-label="Permalink to &quot;Agent Access to Tickets&quot;">​</a></h2><p>When linked, agents can:</p><h3 id="read-ticket-details" tabindex="-1">Read Ticket Details <a class="header-anchor" href="#read-ticket-details" aria-label="Permalink to &quot;Read Ticket Details&quot;">​</a></h3><p>The ticket content is available in context:</p><ul><li>Title and description</li><li>Requirements and acceptance criteria</li><li>Subtasks and their status</li><li>Related tickets</li></ul><h3 id="update-tickets-via-mcp" tabindex="-1">Update Tickets via MCP <a class="header-anchor" href="#update-tickets-via-mcp" aria-label="Permalink to &quot;Update Tickets via MCP&quot;">​</a></h3><p>With <code>sciorex-tickets</code> MCP server:</p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">mcpServers</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  - </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">sciorex-tickets</span></span></code></pre></div><p>Agents can:</p><ul><li>Update ticket status</li><li>Mark subtasks complete</li><li>Add comments</li><li>Create related tickets</li></ul><h3 id="example-conversation" tabindex="-1">Example Conversation <a class="header-anchor" href="#example-conversation" aria-label="Permalink to &quot;Example Conversation&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>You: What&#39;s left to do on this ticket?</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Agent: Looking at ticket T-042, here are the remaining subtasks:</span></span>
<span class="line"><span></span></span>
<span class="line"><span>☐ Add image processing (pending)</span></span>
<span class="line"><span>☐ Update profile display components (pending)</span></span>
<span class="line"><span>☐ Write tests (pending)</span></span>
<span class="line"><span></span></span>
<span class="line"><span>The upload UI and backend endpoint are already complete.</span></span>
<span class="line"><span>Should I start with image processing?</span></span>
<span class="line"><span></span></span>
<span class="line"><span>You: Yes, and mark it in progress</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Agent: I&#39;ll update the subtask status and begin implementing...</span></span>
<span class="line"><span>[Uses sciorex_update_subtask to mark &quot;Add image processing&quot; as in_progress]</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Looking at the requirements, we need to generate thumbnails</span></span>
<span class="line"><span>at 64px, 128px, and 256px...</span></span></code></pre></div><h2 id="multiple-sessions-per-ticket" tabindex="-1">Multiple Sessions Per Ticket <a class="header-anchor" href="#multiple-sessions-per-ticket" aria-label="Permalink to &quot;Multiple Sessions Per Ticket&quot;">​</a></h2><p>Tickets can have multiple linked sessions:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>T-042: Avatar Upload Feature</span></span>
<span class="line"><span>├── Session 1: Initial planning (archived)</span></span>
<span class="line"><span>├── Session 2: Backend implementation (archived)</span></span>
<span class="line"><span>├── Session 3: Frontend work (active)</span></span>
<span class="line"><span>└── Session 4: Code review (upcoming)</span></span></code></pre></div><p>This creates a complete history of how the work evolved.</p><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="link-early" tabindex="-1">Link Early <a class="header-anchor" href="#link-early" aria-label="Permalink to &quot;Link Early&quot;">​</a></h3><p>Set ticket context at the start of meaningful work:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>✓ &quot;Help me implement T-042&quot;</span></span>
<span class="line"><span>✗ &quot;Help me with some file upload thing&quot;</span></span></code></pre></div><h3 id="use-descriptive-archives" tabindex="-1">Use Descriptive Archives <a class="header-anchor" href="#use-descriptive-archives" aria-label="Permalink to &quot;Use Descriptive Archives&quot;">​</a></h3><p>When archiving, add notes:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Archive note: &quot;Completed backend API, ready for frontend&quot;</span></span></code></pre></div><h3 id="review-before-closing" tabindex="-1">Review Before Closing <a class="header-anchor" href="#review-before-closing" aria-label="Permalink to &quot;Review Before Closing&quot;">​</a></h3><p>Check all sessions before marking a ticket done:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>1. Open ticket</span></span>
<span class="line"><span>2. Review all linked sessions</span></span>
<span class="line"><span>3. Verify work is complete</span></span>
<span class="line"><span>4. Change status to Done</span></span></code></pre></div><h3 id="create-sub-sessions" tabindex="-1">Create Sub-Sessions <a class="header-anchor" href="#create-sub-sessions" aria-label="Permalink to &quot;Create Sub-Sessions&quot;">​</a></h3><p>For complex tickets, create focused sessions:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Session 1: &quot;Plan T-042 implementation&quot;</span></span>
<span class="line"><span>Session 2: &quot;Implement T-042 backend&quot;</span></span>
<span class="line"><span>Session 3: &quot;Implement T-042 frontend&quot;</span></span>
<span class="line"><span>Session 4: &quot;Test T-042&quot;</span></span></code></pre></div><h2 id="automatic-linking" tabindex="-1">Automatic Linking <a class="header-anchor" href="#automatic-linking" aria-label="Permalink to &quot;Automatic Linking&quot;">​</a></h2><h3 id="from-ticket-triggers" tabindex="-1">From Ticket Triggers <a class="header-anchor" href="#from-ticket-triggers" aria-label="Permalink to &quot;From Ticket Triggers&quot;">​</a></h3><p>Flows triggered by tickets are automatically linked:</p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">trigger</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  type</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">ticketCreated</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">  # Sessions started by this flow link to the triggering ticket</span></span></code></pre></div><h3 id="from-ticket-mentions" tabindex="-1">From Ticket Mentions <a class="header-anchor" href="#from-ticket-mentions" aria-label="Permalink to &quot;From Ticket Mentions&quot;">​</a></h3><p>Mention a ticket in chat to create a reference:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>You: This relates to T-042 but is a separate concern</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Agent: I&#39;ll note the relationship to T-042.</span></span>
<span class="line"><span>[Adds reference without full linking]</span></span></code></pre></div><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="chat-not-seeing-ticket-context" tabindex="-1">Chat Not Seeing Ticket Context <a class="header-anchor" href="#chat-not-seeing-ticket-context" aria-label="Permalink to &quot;Chat Not Seeing Ticket Context&quot;">​</a></h3><p>Check:</p><ul><li>Session is properly linked (look for ticket badge)</li><li>Agent has Read tool enabled</li><li>Ticket file exists and is valid YAML</li></ul><h3 id="can-t-link-to-ticket" tabindex="-1">Can&#39;t Link to Ticket <a class="header-anchor" href="#can-t-link-to-ticket" aria-label="Permalink to &quot;Can&#39;t Link to Ticket&quot;">​</a></h3><p>Check:</p><ul><li>Ticket exists and isn&#39;t archived</li><li>You have write permissions</li><li>Ticket ID is correct</li></ul><h3 id="agent-can-t-update-ticket" tabindex="-1">Agent Can&#39;t Update Ticket <a class="header-anchor" href="#agent-can-t-update-ticket" aria-label="Permalink to &quot;Agent Can&#39;t Update Ticket&quot;">​</a></h3><p>Check:</p><ul><li><code>sciorex-tickets</code> MCP server is enabled</li><li>Agent has the MCP server configured</li><li>Session has appropriate permissions</li></ul><h2 id="related" tabindex="-1">Related <a class="header-anchor" href="#related" aria-label="Permalink to &quot;Related&quot;">​</a></h2><ul><li><a href="/cookbook/ticket-driven-dev.html">Ticket-Driven Development</a></li><li><a href="/cookbook/epic-planning.html">Epic Planning with AI</a></li><li><a href="/features/ticketing.html">Tickets Reference</a></li><li><a href="/features/chat.html">Chat Reference</a></li></ul>`,82)])])}const g=a(l,[["render",o]]);export{u as __pageData,g as default};
