import{_ as e,c as a,o as r,ag as o}from"./chunks/framework.CQuhCYrb.js";const g=JSON.parse('{"title":"Dashboard","description":"","frontmatter":{},"headers":[],"relativePath":"features/dashboard.md","filePath":"features/dashboard.md"}'),s={name:"features/dashboard.md"};function i(n,t,d,h,l,c){return r(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="dashboard" tabindex="-1">Dashboard <a class="header-anchor" href="#dashboard" aria-label="Permalink to &quot;Dashboard&quot;">​</a></h1><p>The Dashboard is your home screen in Sciorex. It gives you a quick overview of your workspace and fast access to recent items.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>When you open Sciorex, the Dashboard shows:</p><ul><li><strong>Welcome message</strong> with your workspace name</li><li><strong>Quick stats</strong> about your tickets, flows, and agents</li><li><strong>Recent items</strong> for fast navigation</li><li><strong>Quick actions</strong> to create new items</li></ul><h2 id="stats-cards" tabindex="-1">Stats Cards <a class="header-anchor" href="#stats-cards" aria-label="Permalink to &quot;Stats Cards&quot;">​</a></h2><p>The dashboard displays summary statistics:</p><table tabindex="0"><thead><tr><th>Card</th><th>What it shows</th></tr></thead><tbody><tr><td><strong>Tickets</strong></td><td>Total count with breakdown by status</td></tr><tr><td><strong>In Progress</strong></td><td>Active work items</td></tr><tr><td><strong>Flows</strong></td><td>Number of configured flows</td></tr><tr><td><strong>Agents</strong></td><td>Number of configured agents</td></tr></tbody></table><p>Each card shows the change since your last visit (e.g., &quot;+2&quot; for new items).</p><h2 id="recent-items" tabindex="-1">Recent Items <a class="header-anchor" href="#recent-items" aria-label="Permalink to &quot;Recent Items&quot;">​</a></h2><p>The recent items list shows your most recently accessed:</p><ul><li><strong>Tickets</strong> with current status</li><li><strong>Chat sessions</strong> with last message preview</li><li><strong>Flows</strong> with last run status</li><li><strong>Agents</strong> with description</li></ul><p>Click any item to navigate directly to it.</p><h2 id="quick-actions" tabindex="-1">Quick Actions <a class="header-anchor" href="#quick-actions" aria-label="Permalink to &quot;Quick Actions&quot;">​</a></h2><p>Start new work quickly:</p><table tabindex="0"><thead><tr><th>Action</th><th>What it does</th></tr></thead><tbody><tr><td><strong>New Ticket</strong></td><td>Opens ticket creation form</td></tr><tr><td><strong>New Chat</strong></td><td>Starts a new conversation</td></tr><tr><td><strong>New Flow</strong></td><td>Opens the flow editor</td></tr><tr><td><strong>New Agent</strong></td><td>Opens agent creation wizard</td></tr></tbody></table><h2 id="navigation" tabindex="-1">Navigation <a class="header-anchor" href="#navigation" aria-label="Permalink to &quot;Navigation&quot;">​</a></h2><p>From the Dashboard, you can:</p><ul><li>Click <strong>View All</strong> on any section to see the full list</li><li>Click individual items to open their details</li><li>Use the sidebar to navigate to other views</li><li>Use keyboard shortcuts (Ctrl+1 through Ctrl+8) to switch views</li></ul><h2 id="keyboard-shortcut" tabindex="-1">Keyboard Shortcut <a class="header-anchor" href="#keyboard-shortcut" aria-label="Permalink to &quot;Keyboard Shortcut&quot;">​</a></h2><p>Press <code>Ctrl+1</code> (or <code>Cmd+1</code> on Mac) to return to the Dashboard from anywhere.</p>',21)])])}const b=e(s,[["render",i]]);export{g as __pageData,b as default};
