import{_ as e,c as d,o as r,ag as o}from"./chunks/framework.CQuhCYrb.js";const g=JSON.parse('{"title":"Settings Reference","description":"","frontmatter":{},"headers":[],"relativePath":"guide/settings.md","filePath":"guide/settings.md"}'),a={name:"guide/settings.md"};function n(i,t,s,l,h,c){return r(),d("div",null,[...t[0]||(t[0]=[o(`<h1 id="settings-reference" tabindex="-1">Settings Reference <a class="header-anchor" href="#settings-reference" aria-label="Permalink to &quot;Settings Reference&quot;">​</a></h1><p>All settings are accessible via <strong>Settings</strong> (<code>Ctrl/Cmd + ,</code>). Changes are saved automatically to your workspace.</p><h2 id="appearance" tabindex="-1">Appearance <a class="header-anchor" href="#appearance" aria-label="Permalink to &quot;Appearance&quot;">​</a></h2><p>Customize how Sciorex looks.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Color Theme</strong></td><td>Application color scheme</td><td>Dark</td><td>Dark, Light, System</td></tr><tr><td><strong>Accent Color</strong></td><td>Primary highlight color</td><td>Purple</td><td>Purple, Blue, Green, Orange, Red, Pink, Cyan, Yellow</td></tr><tr><td><strong>UI Font Size</strong></td><td>Base font size for the interface</td><td>Medium (14px)</td><td>Small (13px), Medium (14px), Large (16px), XL (18px)</td></tr><tr><td><strong>Code Font Size</strong></td><td>Font size for code blocks and terminal</td><td>14px</td><td>12, 14, 16, 18</td></tr><tr><td><strong>Sidebar Width</strong></td><td>Navigation sidebar width</td><td>Default (256px)</td><td>Narrow (200px), Default (256px), Wide (320px), Extra Wide (400px)</td></tr><tr><td><strong>Reduce Motion</strong></td><td>Minimize animations</td><td>Off</td><td>On, Off</td></tr><tr><td><strong>Process Explorer Mode</strong></td><td>How Process Explorer opens</td><td>Modal</td><td>Modal (current window), Window (new window)</td></tr></tbody></table><p>See <a href="/features/process-explorer.html">Process Explorer</a> for details on monitoring running processes.</p><h2 id="editor" tabindex="-1">Editor <a class="header-anchor" href="#editor" aria-label="Permalink to &quot;Editor&quot;">​</a></h2><p>Code editor behavior and appearance.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Vim Mode</strong></td><td>Enable vim keybindings</td><td>Off</td><td>On, Off</td></tr><tr><td><strong>Editor Theme</strong></td><td>Code editor color scheme</td><td>Tokyo Night</td><td>Tokyo Night, GitHub Light, High Contrast Dark, High Contrast Light</td></tr><tr><td><strong>Insert Spaces</strong></td><td>Use spaces instead of tabs</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Tab Size</strong></td><td>Spaces per indentation level</td><td>2</td><td>2, 4, 8</td></tr><tr><td><strong>Word Wrap</strong></td><td>Wrap long lines</td><td>Off</td><td>On, Off</td></tr><tr><td><strong>Line Numbers</strong></td><td>Show line numbers</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Minimap</strong></td><td>Show code overview on right</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Single Click to Open</strong></td><td>Open files with single click</td><td>Off</td><td>On, Off</td></tr></tbody></table><h2 id="ai-providers" tabindex="-1">AI Providers <a class="header-anchor" href="#ai-providers" aria-label="Permalink to &quot;AI Providers&quot;">​</a></h2><p>Configure AI providers and models.</p><h3 id="provider-settings" tabindex="-1">Provider Settings <a class="header-anchor" href="#provider-settings" aria-label="Permalink to &quot;Provider Settings&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Default Provider</strong></td><td>AI provider for new chats</td><td>Claude Code</td><td>Claude Code, Gemini, Codex, LM Studio, Ollama</td></tr><tr><td><strong>Default Model</strong></td><td>Model for new chats</td><td>Provider-specific</td><td>See <a href="/reference/models.html">Models Reference</a></td></tr><tr><td><strong>Default Permission Mode</strong></td><td>How tool calls are approved</td><td>Auto-approve edits</td><td>Normal (ask all), Auto-approve edits, YOLO (approve all)</td></tr><tr><td><strong>Default Thinking Level</strong></td><td>Extended thinking depth</td><td>Off</td><td>Off, Think, Think Hard, Think Harder, Ultrathink</td></tr><tr><td><strong>MCP Permission Prompts</strong></td><td>Experimental proactive permissions</td><td>Off</td><td>On, Off</td></tr></tbody></table><h3 id="provider-configuration" tabindex="-1">Provider Configuration <a class="header-anchor" href="#provider-configuration" aria-label="Permalink to &quot;Provider Configuration&quot;">​</a></h3><p>Each provider has its own configuration tab:</p><table tabindex="0"><thead><tr><th>Provider</th><th>Configuration</th></tr></thead><tbody><tr><td><strong>Claude Code</strong></td><td>CLI path (auto-detect or custom)</td></tr><tr><td><strong>Google Gemini</strong></td><td>CLI path (auto-detect or custom)</td></tr><tr><td><strong>OpenAI Codex</strong></td><td>CLI path, OSS mode toggle</td></tr><tr><td><strong>LM Studio</strong></td><td>Server URL (default: <code>http://localhost:1234</code>)</td></tr><tr><td><strong>Ollama</strong></td><td>Server URL (default: <code>http://localhost:11434</code>)</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>See the setup instructions in each provider&#39;s tab for installation and authentication details.</p></div><h3 id="system-prompt-override" tabindex="-1">System Prompt Override <a class="header-anchor" href="#system-prompt-override" aria-label="Permalink to &quot;System Prompt Override&quot;">​</a></h3><p>Add custom instructions to every conversation:</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th></tr></thead><tbody><tr><td><strong>Custom Instructions</strong></td><td>Text added to all conversations</td></tr><tr><td><strong>Mode</strong></td><td>Append (add to default) or Replace (override default)</td></tr><tr><td><strong>Templates</strong></td><td>Save and load reusable prompt templates</td></tr></tbody></table><h3 id="thinking-token-budgets" tabindex="-1">Thinking Token Budgets <a class="header-anchor" href="#thinking-token-budgets" aria-label="Permalink to &quot;Thinking Token Budgets&quot;">​</a></h3><p>Configure maximum tokens for each thinking level:</p><table tabindex="0"><thead><tr><th>Level</th><th>Default Budget</th><th>Range</th></tr></thead><tbody><tr><td>Think</td><td>1,024</td><td>1,024 to 128,000</td></tr><tr><td>Think Hard</td><td>10,000</td><td>1,024 to 128,000</td></tr><tr><td>Think Harder</td><td>16,000</td><td>1,024 to 128,000</td></tr><tr><td>Ultrathink</td><td>32,000</td><td>1,024 to 128,000</td></tr></tbody></table><h3 id="system-prompt-override-1" tabindex="-1">System Prompt Override <a class="header-anchor" href="#system-prompt-override-1" aria-label="Permalink to &quot;System Prompt Override&quot;">​</a></h3><p>Add custom instructions included in every conversation:</p><ul><li><strong>Mode</strong>: Append (add after default) or Replace (override default)</li><li><strong>Templates</strong>: Save and load reusable prompt templates</li></ul><h2 id="chat" tabindex="-1">Chat <a class="header-anchor" href="#chat" aria-label="Permalink to &quot;Chat&quot;">​</a></h2><p>Chat interface behavior.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Default Display Mode</strong></td><td>How messages are shown</td><td>Compact</td><td>Extended (full details), Compact (collapsed tools), Focus (minimal)</td></tr><tr><td><strong>Expand Tool Calls</strong></td><td>Auto-expand tool details in Extended mode</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Default Diff View</strong></td><td>How file edits display</td><td>Unified</td><td>Split (side by side), Unified (inline)</td></tr><tr><td><strong>Send Shortcut</strong></td><td>How to send messages</td><td>Enter</td><td>Enter, Shift+Enter, Ctrl+Enter, Cmd+Enter</td></tr><tr><td><strong>Preprocess @ Mentions</strong></td><td>Resolve @file mentions before sending</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Preprocess / Commands</strong></td><td>Execute slash commands via CLI</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Open Links Externally</strong></td><td>Open links in system browser</td><td>Off</td><td>On, Off</td></tr><tr><td><strong>Internal Browser User Agent</strong></td><td>Custom user agent for internal browser</td><td>Default</td><td>Custom string or preset</td></tr></tbody></table><p>See <a href="/features/chat.html">Chat Interface</a> for full chat documentation.</p><h2 id="files" tabindex="-1">Files <a class="header-anchor" href="#files" aria-label="Permalink to &quot;Files&quot;">​</a></h2><p>File tree behavior.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Respect .gitignore</strong></td><td>Hide gitignored files</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Exclude Patterns</strong></td><td>Additional patterns to hide</td><td>node_modules, .git, .sciorex, .tickets</td><td>Custom patterns</td></tr></tbody></table><p>Common patterns you can add: <code>__pycache__</code>, <code>.cache</code>, <code>.DS_Store</code>, <code>vendor</code>, <code>target</code>, <code>.idea</code>, <code>.vscode</code>, <code>*.log</code>, <code>tmp</code>, <code>temp</code></p><h2 id="tickets" tabindex="-1">Tickets <a class="header-anchor" href="#tickets" aria-label="Permalink to &quot;Tickets&quot;">​</a></h2><p>Ticket system configuration.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Ticket Prefix</strong></td><td>Prefix for ticket IDs</td><td>T</td><td>Up to 5 characters</td></tr><tr><td><strong>Epic Prefix</strong></td><td>Prefix for epic IDs</td><td>E</td><td>Up to 5 characters</td></tr><tr><td><strong>Auto-increment Start</strong></td><td>Starting number for IDs</td><td>1</td><td>Any number</td></tr><tr><td><strong>Default Flow</strong></td><td>Flow to run on tickets</td><td>None</td><td>Any flow ID</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Changing prefixes does not rename existing tickets. Only new tickets use the new prefix.</p></div><h2 id="worktrees" tabindex="-1">Worktrees <a class="header-anchor" href="#worktrees" aria-label="Permalink to &quot;Worktrees&quot;">​</a></h2><p>Git worktree support for parallel sessions.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th><th>Options</th></tr></thead><tbody><tr><td><strong>Enable Worktrees</strong></td><td>Turn worktree support on/off</td><td>On</td><td>On, Off</td></tr><tr><td><strong>Git Path</strong></td><td>Path to Git executable</td><td>Auto-detect</td><td>Custom path</td></tr><tr><td><strong>Directory Prefix</strong></td><td>Prefix for worktree folders</td><td>wt-</td><td>Custom string</td></tr></tbody></table><p>Worktrees are stored in <code>.sciorex/worktrees/</code>. See <a href="/guide/concepts/worktrees.html">Understanding Worktrees</a> for details.</p><h2 id="mcp-servers" tabindex="-1">MCP Servers <a class="header-anchor" href="#mcp-servers" aria-label="Permalink to &quot;MCP Servers&quot;">​</a></h2><p>Configure Model Context Protocol servers.</p><p><strong>Built-in servers</strong> (always available):</p><ul><li><code>sciorex-tickets</code> — Ticket and epic management</li><li><code>sciorex-interactions</code> — User prompts and notifications</li></ul><p><strong>Custom servers</strong>: Add your own MCP servers with:</p><ul><li>Server name</li><li>Command (e.g., <code>node</code>, <code>npx</code>, <code>python</code>)</li><li>Arguments</li></ul><h2 id="notifications" tabindex="-1">Notifications <a class="header-anchor" href="#notifications" aria-label="Permalink to &quot;Notifications&quot;">​</a></h2><p>Control when and how you&#39;re notified.</p><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th></tr></thead><tbody><tr><td><strong>Sound Notifications</strong></td><td>Play sound on events</td><td>On</td></tr><tr><td><strong>Desktop Notifications</strong></td><td>Show system notifications</td><td>On</td></tr><tr><td><strong>Permission Requests</strong></td><td>Notify when AI needs approval</td><td>On</td></tr><tr><td><strong>Interaction Requests</strong></td><td>Notify when AI asks questions</td><td>On</td></tr><tr><td><strong>Chat Complete</strong></td><td>Notify when response finishes</td><td>On</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Notifications only trigger when the application window is not focused.</p></div><h2 id="updates" tabindex="-1">Updates <a class="header-anchor" href="#updates" aria-label="Permalink to &quot;Updates&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th><th>Default</th></tr></thead><tbody><tr><td><strong>Auto Update</strong></td><td>Check and download updates automatically</td><td>On</td></tr></tbody></table><h2 id="advanced" tabindex="-1">Advanced <a class="header-anchor" href="#advanced" aria-label="Permalink to &quot;Advanced&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Setting</th><th>Description</th></tr></thead><tbody><tr><td><strong>Prompt Template</strong></td><td>Edit the workspace prompt template (<code>.sciorex/prompt-template.md</code>)</td></tr></tbody></table><h2 id="keyboard-shortcuts" tabindex="-1">Keyboard Shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to &quot;Keyboard Shortcuts&quot;">​</a></h2><p>Quick reference for keyboard shortcuts:</p><h3 id="navigation" tabindex="-1">Navigation <a class="header-anchor" href="#navigation" aria-label="Permalink to &quot;Navigation&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td>Ctrl+1</td><td>Dashboard</td></tr><tr><td>Ctrl+2</td><td>Tickets</td></tr><tr><td>Ctrl+3</td><td>Flow Editor</td></tr><tr><td>Ctrl+4</td><td>Agents</td></tr><tr><td>Ctrl+5</td><td>Chat</td></tr><tr><td>Ctrl+B</td><td>Toggle sidebar</td></tr><tr><td>Ctrl+,</td><td>Settings</td></tr></tbody></table><h3 id="editor-1" tabindex="-1">Editor <a class="header-anchor" href="#editor-1" aria-label="Permalink to &quot;Editor&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td>Ctrl+S</td><td>Save file</td></tr><tr><td>Ctrl+F</td><td>Find</td></tr><tr><td>Ctrl+H</td><td>Find and replace</td></tr><tr><td>Ctrl+G</td><td>Go to line</td></tr><tr><td>Ctrl+/</td><td>Toggle comment</td></tr><tr><td>Ctrl+D</td><td>Select next occurrence</td></tr></tbody></table><h3 id="chat-1" tabindex="-1">Chat <a class="header-anchor" href="#chat-1" aria-label="Permalink to &quot;Chat&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td>Tab</td><td>Cycle thinking levels</td></tr><tr><td>Shift+Tab</td><td>Cycle permission modes</td></tr><tr><td>Escape</td><td>Close modals</td></tr></tbody></table><h3 id="application" tabindex="-1">Application <a class="header-anchor" href="#application" aria-label="Permalink to &quot;Application&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td>Ctrl+N</td><td>New ticket</td></tr><tr><td>Ctrl+T</td><td>New chat</td></tr><tr><td>F11</td><td>Toggle fullscreen</td></tr><tr><td>Ctrl+Plus</td><td>Zoom in</td></tr><tr><td>Ctrl+Minus</td><td>Zoom out</td></tr><tr><td>Ctrl+0</td><td>Reset zoom</td></tr></tbody></table><h2 id="configuration-files" tabindex="-1">Configuration Files <a class="header-anchor" href="#configuration-files" aria-label="Permalink to &quot;Configuration Files&quot;">​</a></h2><p>Settings are stored in your workspace:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.sciorex/</span></span>
<span class="line"><span>├── config.json          # All settings</span></span>
<span class="line"><span>├── prompt-template.md   # Custom prompt template</span></span>
<span class="line"><span>└── worktrees/           # Worktree registry</span></span></code></pre></div><p>You can edit these files directly, but using the Settings UI is recommended.</p>`,70)])])}const u=e(a,[["render",n]]);export{g as __pageData,u as default};
